import './App.css';
import React, { useState } from 'react';

function App() {
  //use hooks
  const[newItem, setNewItem] = useState("");


  return (
    <div className="App">
      {/* The Head */}
      <h1>To Do List With React Hooks</h1>
      {/* The Input */}
      <input
        type='text'
        placeholder='Add an item...'
        value={newItem}
        onChange={e=>{setNewItem(e.target.value)}}
      />
      {/* Add Button */}
      <button>Add</button>
      {/* List of items */}
      <ul>
        <li>Read three pages</li>
        <li>Coding</li>
        <li>Find a football club</li>
      </ul>
    </div>
  );
}

export default App;
