import './App.css';
import React, { useState } from 'react';

function App() {
  //use hooks
  const[newItem, setNewItem] = useState("");
  const[itemList, setItemList] = useState([])

  function addItem(){
    if(!newItem){
      alert("Please enter an item!")
      return
    }
    const item = {
      id: Math.floor(Math.random() * 1000),
      value: newItem
    }
    setItemList(oldList => [...oldList, item])
    setNewItem("")
  }
