  }

  return (
    <div className="App">
      {/* The Head */}
      <h1>To Do List With React Hooks</h1>
      {/* The Input */}
      <input
        type='text'
        placeholder='Add an item...'
        value={newItem}
        onChange={e=>{setNewItem(e.target.value)}}
      />
      {/* Add Button */}
      <button
        onClick = {addItem}
      >Add</button>
      {/* List of items */}
      <ul>
        {itemList.map(item => {
          return(
            <li key={item.id}>{item.value}
            <button onClick={deleteItem(item.id)}>X</button>
            </li>
          )})}
      </ul>
    </div>
  );
}

export default App;
